function showSchedule(){for(var e=document.getElementById("schedule_container"),a=0;a<schedule.length;a++){var t='<div id="'+a+'" class="row lastSchedule box-schedule _fade _shadow"><div class="col-xs-12"><div class="col-sm-4 _bg-sec -image"><img src="image/schedule/schedule'+(a+1)+'.jpg"></div><div class="col-sm-8 -content"><div class="box-title"><div class="row"><div class="col-xs-6"><h1 class="-title">'+schedule[a].name+'</h1></div><p class="col-xs-6 small text-right"><span class="-date">'+schedule[a].date+'</span></p><p class="col-xs-12">'+schedule[a].description+"</p></div></div></div></div></div>";e.innerHTML+=t}}var schedule=[{id:"0",name:"วันสงกรานต์",date:"13-15 เมษายน 2559",description:'วันสงกรานต์ หรือ วันปีใหม่ไทย ของภาคกลาง จะเริ่มขึ้นในวันที่ 13 เมษายน เรามักเรียกวันนี้ว่า "วันมหาสงกรานต์" ส่วนวันที่ 14 เรียกเป็น "วันกลาง" หรือ "วันเนา" และในวันที่ 15 ถือเป็นวัน "วันเถลิงศก" โดยทั้ง 3 วันนี้ ชาวภาคกลางส่วนใหญ่จะประกอบพิธีทางศาสนา มีการทำบุญตักบาตร ปล่อยนก ปล่อยปลา กรวดน้ำอุทิศส่วนกุศลให้แก่ญาติผู้ล่วงลับ มีการสรงน้ำพระ มีการก่อพระเจดีย์ทรายภายในวัด มีการรดน้ำดำหัวผู้ใหญ่',content:'<span class="_tab">วันสงกรานต์</span> หรือ วันปีใหม่ไทย ของภาคกลาง จะเริ่มขึ้นในวันที่ 13 เมษายน เรามักเรียกวันนี้ว่า "วันมหาสงกรานต์" ส่วนวันที่ 14 เรียกเป็น "วันกลาง" หรือ "วันเนา" และในวันที่ 15 ถือเป็นวัน "วันเถลิงศก"  โดยทั้ง 3 วันนี้ ชาวภาคกลางส่วนใหญ่จะประกอบพิธีทางศาสนา มีการทำบุญตักบาตร ปล่อยนก ปล่อยปลา กรวดน้ำอุทิศส่วนกุศลให้แก่ญาติผู้ล่วงลับ มีการสรงน้ำพระ มีการก่อพระเจดีย์ทรายภายในวัด มีการรดน้ำดำหัวผู้ใหญ่ นอกจากนี้ยังมีการทำความสะอาดบ้านเรือนที่อาศัยตลอดจนบริเวณใกล้เคียง โดยเฉพาะอย่างยิ่งที่บูชาพระและที่เก็บอัฐิบรรพบุรุษ เพื่อให้สิ่งสกปรกหมดไปพร้อมกับปีเก่า เพื่อเป็นการต้อนรับปีใหม่ไทย ด้วยความบริสุทธิ์ผุดผ่องนั่นเอง'},{id:"1",name:"วันวิสาขบูชา",date:"20 พฤษภาคม 2559",description:"วันวิสาขบูชา ถือเป็นวันสำคัญที่สุดทางพระพุทธศาสนา เนื่องจากล้วนมีเหตุการณ์ที่เกี่ยวข้องกับการถือกำเนิดของพระพุทธศาสนา คือ เป็นวันที่พระศาสดา คือ พระสัมมาสัมพุทธเจ้าประสูติ ตรัสรู้ และปรินิพพาน ดังนั้นพุทธศาสนิกชนทั่วโลกจึงให้ความสำคัญกับวันวิสาขบูชานี้",content:'<span class="_tab">วันวิสาขบูชา</span> ถือเป็นวันสำคัญที่สุดทางพระพุทธศาสนา เนื่องจากล้วนมีเหตุการณ์ที่เกี่ยวข้องกับการถือกำเนิดของพระพุทธศาสนา คือ เป็นวันที่พระศาสดา คือ พระสัมมาสัมพุทธเจ้าประสูติ ตรัสรู้ และปรินิพพาน ดังนั้นพุทธศาสนิกชนทั่วโลกจึงให้ความสำคัญกับวันวิสาขบูชานี้ <br><br><strong>กิจกรรมที่พุทธศาสนิกชนพึงปฏิบัติใน วันวิสาขบูชา ได้แก่</strong> <br>1. ทำบุญใส่บาตร กรวดน้ำอุทิศส่วนกุศลให้ญาติที่ล่วงลับ และเจ้ากรรมนายเวร <br>2. จัดสำรับคาวหวานไปทำบุญถวายภัตตาหารที่วัด และปฏิบัติธรรม ฟังพระธรรมเทศนา <br>3. ร่วมเวียนเทียนรอบอุโบสถที่วัดในตอนค่ำ เพื่อรำลึกถึงพระพุทธ พระธรรม พระสงฆ์'},{id:"2",name:"วันอาสาฬหบูชา",date:"19 กรกฎาคม 2559",description:"วันอาสาฬหบูชา คือวันที่พระพุทธเจ้าได้ทรงประกาศพระพุทธศาสนาเป็นครั้งแรก หลังจากตรัสรู้ได้ 2 เดือน โดยแสดงปฐมเทศนาโปรดพระปัญจวัคคีย์ทั้ง 5 ได้แก่ พระโกณฑัญญะ พระวัปปะ พระภัททิยะ พระมหานามะ และพระอัสสชิ ที่ป่าอิสิปตนมฤคทายวัน เมืองพาราณสี แคว้นมคธ จน พระอัญญาโกณฑัญญะ ได้บรรลุธรรมและขอบวชเป็นพระภิกษุรูปแรกในพระพุทธศาสนา จึงถือว่าวันนี้มีพระรัตนตรัยครบองค์สามบริบูรณ์ครั้งแรกในโลก คือ มีทั้งพระพุทธ พระธรรม และพระสงฆ์ ซึ่งเหตุการณ์นี้เกิดขึ้นก่อนพุทธศักราช 45 ปี",content:'<span class="_tab">วันอาสาฬหบูชา</span> คือวันที่พระพุทธเจ้าได้ทรงประกาศพระพุทธศาสนาเป็นครั้งแรก หลังจากตรัสรู้ได้ 2 เดือน โดยแสดงปฐมเทศนาโปรดพระปัญจวัคคีย์ทั้ง 5 ได้แก่ พระโกณฑัญญะ พระวัปปะ พระภัททิยะ พระมหานามะ และพระอัสสชิ ที่ป่าอิสิปตนมฤคทายวัน เมืองพาราณสี แคว้นมคธ จน พระอัญญาโกณฑัญญะ ได้บรรลุธรรมและขอบวชเป็นพระภิกษุรูปแรกในพระพุทธศาสนา จึงถือว่าวันนี้มีพระรัตนตรัยครบองค์สามบริบูรณ์ครั้งแรกในโลก คือ มีทั้งพระพุทธ พระธรรม และพระสงฆ์ ซึ่งเหตุการณ์นี้เกิดขึ้นก่อนพุทธศักราช 45 ปี <br><br><strong>กิจกรรมวันอาสาฬหบูชา</strong> <br>1.การทำบุญ ตักบาตร <br>2.ฟังพระธรรมเทศนา และสวดมนต์ <br>3.ในตอนค่ำก็จะมีการเวียนเทียน'},{id:"3",name:"วันเข้าพรรษา",date:"20 กรกฎาคม 2559",description:'เข้าพรรษา แปลว่า พักฝน หมายถึง พระภิกษุสงฆ์ต้องอยู่ประจำ ณ วัดใดวัดหนึ่งระหว่างฤดูฝน โดยเหตุที่พระภิกษุในสมัยพุทธกาล มีหน้าที่จะต้องจาริกโปรดสัตว์ และเผยแผ่พระธรรมคำสั่งสอนแก่ประชาชนไปในที่ต่าง ๆ ไม่จำเป็นต้องมีที่อยู่ประจำ แม้ในฤดูฝน ชาวบ้านจึงตำหนิว่าไปเหยียบข้าวกล้าและพืชอื่น ๆ จนเสียหาย พระพุทธเจ้าจึงทรงวางระเบียบการจำพรรษาให้พระภิกษุอยู่ประจำที่ตลอด 3 เดือน ในฤดูฝน คือ เริ่มตั้งแต่วันแรม 1 ค่ำ เดือน 8 ของทุกปี เรียกว่า "ปุริมพรรษา"',content:'<span class="_tab">เข้าพรรษา</span> แปลว่า พักฝน หมายถึง พระภิกษุสงฆ์ต้องอยู่ประจำ ณ วัดใดวัดหนึ่งระหว่างฤดูฝน โดยเหตุที่พระภิกษุในสมัยพุทธกาล มีหน้าที่จะต้องจาริกโปรดสัตว์ และเผยแผ่พระธรรมคำสั่งสอนแก่ประชาชนไปในที่ต่าง ๆ ไม่จำเป็นต้องมีที่อยู่ประจำ แม้ในฤดูฝน ชาวบ้านจึงตำหนิว่าไปเหยียบข้าวกล้าและพืชอื่น ๆ จนเสียหาย พระพุทธเจ้าจึงทรงวางระเบียบการจำพรรษาให้พระภิกษุอยู่ประจำที่ตลอด 3 เดือน ในฤดูฝน คือ เริ่มตั้งแต่วันแรม 1 ค่ำ เดือน 8 ของทุกปี เรียกว่า "ปุริมพรรษา" <br><br><span class="_tab">ถ้าปีใดมีเดือน</span> 8 สองครั้ง ก็เลื่อนมาเป็นวันแรม 1 ค่ำ เดือนแปดหลัง และออกพรรษาในวันขึ้น 15 ค่ำ เดือน 11 เรียกว่า "ปัจฉิมพรรษา" เว้นแต่มีกิจธุระคือเมื่อเดินทางไปแล้วไม่สามารถจะกลับได้ในเดียวนั้น ก็ทรงอนุญาตให้ไปแรมคืนได้ คราวหนึ่งไม่เกิน 7 คืน เรียกว่า "สัตตาหะ" หากเกินกำหนดนี้ถือว่าไม่ได้รับประโยชน์แห่งการจำพรรษา จัดว่าพรรษาขาด <br><br><strong>กิจกรรมในวันเข้าพรรษา</strong> <br>ร่วมทำบุญ ตักบาตร ฟังธรรมเทศนา รักษาอุโบสถศีล'},{id:"4",name:"วันออกพรรษา",date:"16 ตุลาคม 2559",description:"วันออกพรรษา พระสงฆ์จะประกอบพิธีทำสังฆกรรมใหญ่ที่เรียกว่า มหาปวารณา ใน วันออกพรรษา ซึ่งเป็นการเปิดโอกาสให้ภิกษุว่ากล่าวตักเตือนซึ่งกันและกันได้ เนื่องจากในระหว่างที่เข้าพรรษาอยู่ด้วยกัน พระสงฆ์บางรูปอาจมีข้อบกพร่องที่ต้องแก้ไข และการให้ผู้อื่นว่ากล่าวตักเตือนก็จะทำให้รู้ข้อบกพร่องของตน อีกทั้งยังเปิดโอกาสให้ถามข้อสงสัยซึ่งกันและกันได้ด้วย พระผู้ใหญ่ก็กล่าวตักเตือนพระผู้น้อยได้ และพระผู้มีอาวุโสน้อยก็สามารถชี้แนะถึงข้อไม่ดีของพระผู้ใหญ่ได้เช่นกัน",content:'<span class="_tab">วันออกพรรษา</span> พระสงฆ์จะประกอบพิธีทำสังฆกรรมใหญ่ที่เรียกว่า มหาปวารณา ใน วันออกพรรษา ซึ่งเป็นการเปิดโอกาสให้ภิกษุว่ากล่าวตักเตือนซึ่งกันและกันได้ เนื่องจากในระหว่างที่เข้าพรรษาอยู่ด้วยกัน พระสงฆ์บางรูปอาจมีข้อบกพร่องที่ต้องแก้ไข และการให้ผู้อื่นว่ากล่าวตักเตือนก็จะทำให้รู้ข้อบกพร่องของตน อีกทั้งยังเปิดโอกาสให้ถามข้อสงสัยซึ่งกันและกันได้ด้วย พระผู้ใหญ่ก็กล่าวตักเตือนพระผู้น้อยได้ และพระผู้มีอาวุโสน้อยก็สามารถชี้แนะถึงข้อไม่ดีของพระผู้ใหญ่ได้เช่นกัน แม้พระผู้ใหญ่จะมีอาวุโสมากกว่า แต่ท่านก็มิได้สำคัญตนผิดคิดว่าท่านทำอะไรแล้วถูกไปหมดทุกอย่าง <br><br><strong>กิจกรรมต่างๆในวันออกพรรษา</strong> <br>1. ทำบุญตักบาตรอุทิศส่วนกุศลให้แก่ญาติผู้ล่วงลับ <br>2. ฟังพระธรรมเทศนา รักษาศีล ถวายสังฆทาน ถวายภัตตาหาร หรือจัดดอกไม้ ธูป เทียน ไปบูชาที่วัด และฟังพระธรรมเทศนา <br>3. ร่วมกุศลธรรม "ตักบาตรเทโว"'}];showSchedule();
